import React, { useState } from 'react';
import './SearchBar.css';

const SearchBar = ({ json_obj, setresult_selected }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);

  const handleInputChange = async (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.length < 2) {
      setResults([]);
      setresult_selected(false);
      return;
    }

    try {
      const response = await fetch(`http://localhost:3000/searchrecipe?query=${encodeURIComponent(value)}`);
      const data = await response.json();
      setResults(data);
      setresult_selected(false);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleResultClick = (result) => {
    json_obj(result);
    setresult_selected(true);
    setSearchTerm(result.title);
    setResults([]);
  };

  return (
    <div className="search-bar" role="search">
      <input
        type="text"
        placeholder="Search recipes..."
        value={searchTerm}
        onChange={handleInputChange}
        aria-label="Search recipes"
        aria-autocomplete="list"
        aria-controls="search-results-list"
        aria-expanded={results.length > 0}
      />
      {results.length > 0 && (
        <ul id="search-results-list" role="listbox" className="results-list">
          {results.map((result, index) => (
            <li
              key={result.id || index}
              role="option"
              tabIndex={0}
              onClick={() => handleResultClick(result)}
              onKeyPress={(e) => { if (e.key === 'Enter') handleResultClick(result); }}
              aria-selected={false}
              className="result-item"
            >
              {result.title}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
